mapper.select:
SELECT *
FROM easyuser
WHERE username = #{username}
  AND password = #{password}
  AND state = 1

SELECT *
FROM easyuser

	WHERE username LIKE #{username}

LIMIT #{pageStart}, #{pageSize}

SELECT count(*)
FROM easyuser

	WHERE username LIKE #{username}

mapper.update:
UPDATE easyuser
SET state = #{state}
WHERE id = #{id}
